CREATE TABLE `eva_user_login_sources` ( `id` int(11) NOT NULL AUTO_INCREMENT, `userId` int(11) NOT NULL, `source` varchar(20) NOT NULL DEFAULT '', `loginAt` int(11) NOT NULL, `remoteIp` varchar(50) DEFAULT NULL, PRIMARY KEY (`id`), KEY `userId` (`userId`))
CREATE TABLE IF NOT EXISTS `eva_user_tokens` ( `sessionId` varchar(40) COLLATE utf8_bin NOT NULL, `token` varchar(32) COLLATE utf8_bin NOT NULL, `userHash` varchar(32) COLLATE utf8_bin NOT NULL, `userId` int(10) NOT NULL, `refreshAt` int(10) NOT NULL, `expiredAt` int(10) NOT NULL, PRIMARY KEY (`sessionId`,`token`,`userHash`))
CREATE TABLE `eva_user_login_records` ( `id` int(11) NOT NULL AUTO_INCREMENT, `userId` int(11) NOT NULL, `source` varchar(20) NOT NULL DEFAULT '', `loginAt` int(11) NOT NULL, `remoteIp` varchar(50) DEFAULT NULL, PRIMARY KEY (`id`), KEY `userId` (`userId`))
CREATE TABLE `eva_user_realname_auth_thread` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `requestTime` int(10) unsigned DEFAULT NULL COMMENT '', `quantity` int(10) unsigned DEFAULT NULL COMMENT '', `status` char(10) COLLATE utf8_unicode_ci DEFAULT NULL, PRIMARY KEY (`id`))
CREATE TABLE `eva_user_realname_auth_log` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `userId` int(10) unsigned DEFAULT NULL, `username` varchar(64) COLLATE utf8_unicode_ci DEFAULT NULL, `realName` char(30) COLLATE utf8_unicode_ci DEFAULT NULL, `cardNum` char(100) COLLATE utf8_unicode_ci DEFAULT NULL, `requestAt` int(10) unsigned DEFAULT NULL COMMENT '', `quantity` int(10) unsigned DEFAULT '0' COMMENT '', `status` char(10) COLLATE utf8_unicode_ci DEFAULT NULL, `compStatus` char(10) COLLATE utf8_unicode_ci DEFAULT NULL, `message` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL, `compMessage` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL, PRIMARY KEY (`id`))
CREATE TABLE `eva_user_realname_auth` ( `userId` int(10) unsigned NOT NULL, `realName` varchar(50) COLLATE utf8_unicode_ci NOT NULL, `cardNum` char(100) COLLATE utf8_unicode_ci NOT NULL, `status` tinyint(10) DEFAULT '0' COMMENT '0321', `createTime` int(10) unsigned DEFAULT NULL, PRIMARY KEY (`userId`))
