CREATE TABLE IF NOT EXISTS `jacobine_git` (	`id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Unique id of gitweb project',	`project` int(11) unsigned NOT NULL COMMENT 'ID field of project table',	`name` varchar(200) DEFAULT NULL COMMENT 'Name of git project',	`git` varchar(200) DEFAULT NULL COMMENT 'Address of git repository',	PRIMARY KEY (`id`),	UNIQUE KEY `git` (`git`))
CREATE TABLE IF NOT EXISTS `jacobine_linguist` (	`id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Unique id of linguist record',	`version` int(11) unsigned NOT NULL COMMENT 'id field of version table',	`percent` decimal(5,2) unsigned DEFAULT '0.00' COMMENT 'Percent of programming language',	`language` varchar(40) DEFAULT '' COMMENT 'Programming language',	PRIMARY KEY (`id`))
CREATE TABLE IF NOT EXISTS `jacobine_project` (	`id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Unique id of a project',	`name` varchar(50) DEFAULT NULL COMMENT 'Name of a project',	`website` varchar(100) DEFAULT NULL COMMENT 'Website of a project',	PRIMARY KEY (`id`))
CREATE TABLE `versions` ( `id` int(11) NOT NULL, `branch` varchar(4) DEFAULT NULL, `version` varchar(13) DEFAULT NULL, `date` varchar(23) DEFAULT NULL, `type` varchar(11) DEFAULT NULL, `checksum_tar_md5` varchar(32) DEFAULT NULL, `checksum_tar_sha1` varchar(40) DEFAULT NULL, `checksum_zip_md5` varchar(32) DEFAULT NULL, `checksum_zip_sha1` varchar(40) DEFAULT NULL, `url_tar` varchar(40) DEFAULT NULL, `url_zip` varchar(40) DEFAULT NULL, `downloaded` tinyint(1) DEFAULT 0, `extracted` tinyint(1) DEFAULT 0, `size_tar` int(11) DEFAULT 0, PRIMARY KEY (`id`) )
CREATE TABLE IF NOT EXISTS `jacobine_datasource` (	`id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Unique id of a data source',	`project` int(11) unsigned NOT NULL COMMENT 'ID field of project table',	`type` tinyint(1) UNSIGNED DEFAULT 0 COMMENT 'Type of a data source',	`content` varchar(100) DEFAULT NULL COMMENT 'Content (Name or URL) of a data source',	PRIMARY KEY (`id`))
