CREATE TABLE `book` ( `id` int NOT NULL AUTO_INCREMENT PRIMARY KEY, `name` varchar(50) NOT NULL, `author` int(11) NOT NULL, FOREIGN KEY (`author`) REFERENCES `author` (`id`))
CREATE TABLE `author` ( `id` int NOT NULL AUTO_INCREMENT PRIMARY KEY, `name` varchar(50) NOT NULL)
CREATE TABLE `person` ( `id` varchar(25) COLLATE utf8_czech_ci NOT NULL, `person1` varchar(25) COLLATE utf8_czech_ci NOT NULL, `person2` varchar(25) COLLATE utf8_czech_ci DEFAULT NULL, PRIMARY KEY (`id`), KEY `person1` (`person1`), KEY `person2` (`person2`), CONSTRAINT `person_ibfk_2` FOREIGN KEY (`person2`) REFERENCES `person` (`id`) ON DELETE SET NULL ON UPDATE CASCADE, CONSTRAINT `person_ibfk_1` FOREIGN KEY (`person1`) REFERENCES `person` (`id`) ON DELETE CASCADE ON UPDATE CASCADE)
CREATE TABLE `category` ( `id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(100) COLLATE utf8_czech_ci NOT NULL, PRIMARY KEY (`id`))
CREATE TABLE `category_closure` ( `ancestor` int(11) NOT NULL, `descendant` int(11) NOT NULL, `depth` int(11) NOT NULL, KEY `ancestor` (`ancestor`), KEY `descendant` (`descendant`), CONSTRAINT `category_closure_ibfk_1` FOREIGN KEY (`ancestor`) REFERENCES `category` (`id`), CONSTRAINT `category_closure_ibfk_2` FOREIGN KEY (`descendant`) REFERENCES `category` (`id`))
