CREATE TABLE `visitors` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT, `user_id` INT(10) UNSIGNED NULL, `purchase_id` INT(10) UNSIGNED NULL, `country_id` INT(10) UNSIGNED NULL, `token` VARCHAR(100), `ip` VARCHAR(100), `currency` VARCHAR(3) NOT NULL, PRIMARY KEY (`id`))
CREATE TABLE `users` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT, `email` VARCHAR(254) NOT NULL, `username` VARCHAR(32) NOT NULL DEFAULT '', `password` VARCHAR(64) NOT NULL, `logins` INT(10) UNSIGNED NOT NULL DEFAULT '0', `last_login` INT(10) UNSIGNED, `facebook_uid` VARCHAR(100), `twitter_uid` VARCHAR(100), `last_login_ip` VARCHAR(40), PRIMARY KEY (`id`), UNIQUE KEY `uniq_username` (`username`), UNIQUE KEY `uniq_email` (`email`))
CREATE TABLE `locations_branches` ( `ansestor_id` int(11) UNSIGNED NOT NULL, `descendant_id` int(11) UNSIGNED NOT NULL, `depth` int(11) UNSIGNED NOT NULL)
CREATE TABLE `locations` ( `id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT, `name` varchar(100) NOT NULL, `short_name` varchar(10) NOT NULL, `type` varchar(100) NOT NULL, PRIMARY KEY (`id`))
