CREATE TABLE `categories` ( `id` int(11) NOT NULL AUTO_INCREMENT, `category_order` int(11) NOT NULL DEFAULT '0', `category` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL, `description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL, `accession` int(4) NOT NULL DEFAULT '0', `thread_count` int(11) NOT NULL DEFAULT '0', PRIMARY KEY (`id`))
CREATE TABLE aros ( id INTEGER(10) UNSIGNED NOT NULL AUTO_INCREMENT, parent_id INTEGER(10) DEFAULT NULL, model VARCHAR(255) DEFAULT '', foreign_key INTEGER(10) UNSIGNED DEFAULT NULL, alias VARCHAR(255) DEFAULT '', lft INTEGER(10) DEFAULT NULL, rght INTEGER(10) DEFAULT NULL, PRIMARY KEY (id))
CREATE TABLE `shouts` ( `id` int(11) NOT NULL AUTO_INCREMENT, `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, `text` varchar(255) DEFAULT NULL, `user_id` int(11) NOT NULL, `time` timestamp NULL DEFAULT NULL, PRIMARY KEY (`id`))
CREATE TABLE IF NOT EXISTS `uploads` ( `id` int(11) unsigned NOT NULL auto_increment, `name` varchar(200) NOT NULL, `type` varchar(200) NOT NULL, `size` int(11) NOT NULL, `created` datetime NOT NULL, `modified` datetime NOT NULL, PRIMARY KEY (`id`))
CREATE TABLE `esnotifications` ( `id` int(11) NOT NULL AUTO_INCREMENT, `user_id` int(11) NOT NULL, `esevent_id` int(11) NOT NULL, `esreceiver_id` int(11) NOT NULL, `deactivate` int(8) NOT NULL, `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, PRIMARY KEY (`id`), KEY `userid_esreceiverid` (`user_id`,`esreceiver_id`), KEY `eseventid_esreceiverid_userid` (`esevent_id`,`esreceiver_id`,`user_id`))
CREATE TABLE i18n (	id int(10) NOT NULL auto_increment,	locale varchar(6) NOT NULL,	model varchar(255) NOT NULL,	foreign_key int(10) NOT NULL,	field varchar(255) NOT NULL,	content mediumtext,	PRIMARY KEY	(id),#	UNIQUE INDEX I18N_LOCALE_FIELD(locale, model, foreign_key, field),#	INDEX I18N_LOCALE_ROW(locale, model, foreign_key),#	INDEX I18N_LOCALE_MODEL(locale, model),#	INDEX I18N_FIELD(model, foreign_key, field),#	INDEX I18N_ROW(model, foreign_key),	INDEX locale (locale),	INDEX model (model),	INDEX row_id (foreign_key),	INDEX field (field))
CREATE TABLE `settings` ( `name` varchar(255) CHARACTER SET utf8 NOT NULL, `value` varchar(255) CHARACTER SET utf8 DEFAULT NULL, PRIMARY KEY (`name`))
CREATE TABLE `useronline` ( `id` int(11) unsigned NOT NULL AUTO_INCREMENT, `uuid` varchar(32) COLLATE utf8_unicode_ci NOT NULL DEFAULT '', `user_id` int(11) DEFAULT NULL, `logged_in` tinyint(1) NOT NULL, `time` int(14) NOT NULL DEFAULT '0', `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, PRIMARY KEY (`id`), UNIQUE KEY `useronline_uuid` (`uuid`), KEY `useronline_userId` (`user_id`), KEY `useronline_loggedIn` (`logged_in`))
CREATE TABLE `smiley_codes` ( `id` int(11) NOT NULL AUTO_INCREMENT, `smiley_id` int(11) NOT NULL DEFAULT '0', `code` varchar(32) CHARACTER SET utf8 DEFAULT NULL, PRIMARY KEY (`id`))
CREATE TABLE `bookmarks` ( `id` int(11) NOT NULL AUTO_INCREMENT, `user_id` int(11) NOT NULL, `entry_id` int(11) NOT NULL, `comment` varchar(255) CHARACTER SET utf8 NOT NULL DEFAULT '', `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, PRIMARY KEY (`id`), KEY `bookmarks_entryId_userId` (`entry_id`,`user_id`), KEY `bookmarks_userId` (`user_id`))
CREATE TABLE `ecaches` ( `created` datetime NOT NULL, `modified` datetime NOT NULL, `key` varchar(128) NOT NULL, `value` mediumblob NOT NULL, PRIMARY KEY (`key`))
CREATE TABLE `uploads` ( `id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL, `type` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL, `size` int(11) DEFAULT NULL, `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, `user_id` int(11) DEFAULT NULL, PRIMARY KEY (`id`))
CREATE TABLE aros_acos ( id INTEGER(10) UNSIGNED NOT NULL AUTO_INCREMENT, aro_id INTEGER(10) UNSIGNED NOT NULL, aco_id INTEGER(10) UNSIGNED NOT NULL, _create CHAR(2) NOT NULL DEFAULT 0, _read CHAR(2) NOT NULL DEFAULT 0, _update CHAR(2) NOT NULL DEFAULT 0, _delete CHAR(2) NOT NULL DEFAULT 0, PRIMARY KEY(id))
CREATE TABLE `user_read` ( `id` int(11) NOT NULL AUTO_INCREMENT, `user_id` int(11) NOT NULL, `entry_id` int(11) NOT NULL, `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, PRIMARY KEY (`id`), KEY `user_id` (`user_id`), KEY `entry_id` (`entry_id`))
CREATE TABLE `esevents` ( `id` int(11) NOT NULL AUTO_INCREMENT, `subject` int(11) NOT NULL, `event` int(11) NOT NULL, `created` datetime DEFAULT NULL, `modified` datetime DEFAULT NULL, PRIMARY KEY (`id`), KEY `subject_event` (`subject`,`event`))
CREATE TABLE `smilies` ( `id` int(11) NOT NULL AUTO_INCREMENT, `sort` int(4) NOT NULL DEFAULT '0', `icon` varchar(100) CHARACTER SET utf8 DEFAULT NULL, `image` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL, `title` varchar(255) CHARACTER SET utf8 DEFAULT NULL, PRIMARY KEY (`id`))
CREATE TABLE cake_sessions ( id varchar(255) NOT NULL default '', data text, expires int(11) default NULL, PRIMARY KEY (id))
CREATE TABLE acos ( id INTEGER(10) UNSIGNED NOT NULL AUTO_INCREMENT, parent_id INTEGER(10) DEFAULT NULL, model VARCHAR(255) DEFAULT '', foreign_key INTEGER(10) UNSIGNED DEFAULT NULL, alias VARCHAR(255) DEFAULT '', lft INTEGER(10) DEFAULT NULL, rght INTEGER(10) DEFAULT NULL, PRIMARY KEY (id))
