CREATE TABLE `category` ( `_id`	 INTEGER PRIMARY KEY AUTOINCREMENT, `id`	 TEXT UNIQUE, `name` TEXT, `notes` TEXT, `order_in_menu` INTEGER, `truck_id` TEXT, FOREIGN KEY(`truck_id`) REFERENCES `truck_properties`(`id`))
CREATE TABLE `menu_item` ( `_id`	 INTEGER PRIMARY KEY AUTOINCREMENT, `id`	 TEXT UNIQUE, `name` TEXT, `price` REAL, `is_available` INTEGER, `notes` TEXT, `tags` TEXT, `order_in_category` INTEGER, `category_id` TEXT, `is_dirty` INTEGER DEFAULT 0, FOREIGN KEY(`category_id`) REFERENCES `category`(`id`))
CREATE TABLE `truck_properties` ( `_id`	 INTEGER PRIMARY KEY AUTOINCREMENT,	`id`	 TEXT UNIQUE,	`name`	 TEXT,	`image_url`	 TEXT,	`keywords`	 TEXT,	`color_primary`	 TEXT,	`color_secondary`	 TEXT,	`phone_number` TEXT,	`description` TEXT)
CREATE TABLE `truck_state` ( `_id`	 INTEGER PRIMARY KEY AUTOINCREMENT, `id` 	 TEXT UNIQUE, `is_serving` INTEGER DEFAULT 0, `matched_search` INTEGER DEFAULT 1, `latitude` REAL, `longitude` REAL, `is_dirty` INTEGER DEFAULT 0, `owned_by_current_user` INTEGER DEFAULT 0)
