CREATE TABLE IF NOT EXISTS `permissions` (`id` int(10) unsigned NOT NULL, `profilesId` int(10) unsigned NOT NULL, `resource` varchar(16) NOT NULL, `action` varchar(16) NOT NULL)
CREATE TABLE IF NOT EXISTS `failed_logins` (`id` int(11) unsigned NOT NULL, `usersId` int(10) unsigned DEFAULT NULL, `ipAddress` char(15) NOT NULL, `attempted` int(11) unsigned NOT NULL)
CREATE TABLE IF NOT EXISTS `posts` (`id` int(10) NOT NULL, `title` varchar(128) NOT NULL, `content` text NOT NULL, `categoriesId` varchar(50) NOT NULL, `slug` varchar(64) NOT NULL, `tags` varchar(50) NOT NULL, `created` int(10) unsigned NOT NULL, `numberViews` int(20) NOT NULL, `userId` int(25) NOT NULL COMMENT 'user post article')
CREATE TABLE IF NOT EXISTS `contact` (`id` int(10) unsigned NOT NULL, `name` varchar(70) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL, `email` varchar(70) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL, `comments` text CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL, `created_at` datetime DEFAULT NULL)
CREATE TABLE IF NOT EXISTS `profiles` (`id` int(10) unsigned NOT NULL, `name` varchar(64) COLLATE utf8_unicode_ci NOT NULL, `active` char(1) COLLATE utf8_unicode_ci NOT NULL)
CREATE TABLE IF NOT EXISTS `categories` (`id` int(10) unsigned NOT NULL, `name` varchar(72) NOT NULL, `slug` varchar(32) DEFAULT NULL, `numberPosts` int(10) unsigned DEFAULT NULL)
CREATE TABLE IF NOT EXISTS `posts_views` (`id` int(10) unsigned NOT NULL, `postsId` int(10) unsigned NOT NULL, `ipaddress` varchar(20) NOT NULL)
CREATE TABLE IF NOT EXISTS `reset_passwords` (`id` int(10) unsigned NOT NULL, `usersId` int(10) unsigned NOT NULL, `code` varchar(48) NOT NULL, `createdAt` int(10) unsigned NOT NULL, `modifiedAt` int(10) unsigned DEFAULT NULL, `reset` char(1) NOT NULL)
CREATE TABLE IF NOT EXISTS `password_changes` (`id` int(10) unsigned NOT NULL, `usersId` int(10) unsigned NOT NULL, `ipAddress` char(15) NOT NULL, `userAgent` varchar(48) NOT NULL, `createdAt` int(10) unsigned NOT NULL)
CREATE TABLE IF NOT EXISTS `file_store` (`id` int(10) NOT NULL, `fileName` varchar(200) NOT NULL, `fileCaption` varchar(200) NOT NULL, `mimeType` varchar(50) NOT NULL, `description` text NOT NULL, `fileData` mediumblob, `categoriesId` int(10) NOT NULL, `userId` int(10) NOT NULL, `created` int(10) NOT NULL)
CREATE TABLE IF NOT EXISTS `remember_tokens` (`id` int(10) unsigned NOT NULL, `usersId` int(10) unsigned NOT NULL, `token` char(32) NOT NULL, `userAgent` varchar(120) NOT NULL, `createdAt` int(10) unsigned NOT NULL)
CREATE TABLE IF NOT EXISTS `success_logins` (`id` int(11) unsigned NOT NULL, `usersId` int(10) unsigned NOT NULL, `ipAddress` char(15) NOT NULL, `userAgent` varchar(120) NOT NULL)
CREATE TABLE IF NOT EXISTS `users` (`id` int(10) unsigned NOT NULL, `username` varchar(40) NOT NULL, `email` varchar(40) NOT NULL, `password` char(60) NOT NULL, `mustChangePassword` char(1) DEFAULT NULL, `profilesId` int(10) unsigned NOT NULL, `fullName` varchar(50) NOT NULL, `banned` char(1) NOT NULL, `suspended` char(1) NOT NULL, `active` char(1) DEFAULT NULL)
CREATE TABLE IF NOT EXISTS `email_confirmations` (`id` int(10) unsigned NOT NULL, `usersId` int(10) unsigned NOT NULL, `code` char(32) NOT NULL, `createdAt` int(10) unsigned NOT NULL, `modifiedAt` int(10) unsigned DEFAULT NULL, `confirmed` char(1) DEFAULT 'N')
