CREATE TABLE {{%customer_note}} ( cno_id INT(11) UNSIGNED NOT NULL AUTO_INCREMENT, cno_prj_id int(11) unsigned NOT NULL, cno_customer_id varchar(128) NOT NULL, cno_created_date DATETIME NOT NULL, cno_updated_date DATETIME NULL, cno_note TEXT, PRIMARY KEY(cno_id), UNIQUE(cno_prj_id, cno_customer_id))
CREATE TABLE {{%project_category}} ( prc_id int(11) unsigned NOT NULL auto_increment, prc_prj_id int(11) unsigned NOT NULL default 0, prc_title varchar(64) NOT NULL default '', PRIMARY KEY (prc_id), UNIQUE KEY uniq_category (prc_prj_id,prc_title), KEY prc_prj_id (prc_prj_id))
CREATE TABLE {{%project_status_date}} ( psd_id INT(11) UNSIGNED NOT NULL auto_increment, psd_prj_id INT(11) UNSIGNED NOT NULL, psd_sta_id INT(10) UNSIGNED NOT NULL, psd_date_field VARCHAR(64) NOT NULL, psd_label VARCHAR(32) NOT NULL, PRIMARY KEY (psd_id), UNIQUE KEY (psd_prj_id, psd_sta_id))
select prj_id from {{%project}} limit 1 into @prj_id;
CREATE TABLE {{%user_alias}} (	ual_usr_id int(11) unsigned not null,	ual_email varchar(255),	PRIMARY KEY(ual_usr_id, ual_email),	UNIQUE(ual_email))
CREATE TABLE {{%user_preference}}( upr_usr_id int(11) unsigned NOT NULL, upr_timezone varchar(100) NOT NULL, upr_week_firstday tinyint(1) NOT NULL DEFAULT 0, upr_list_refresh_rate int(5) DEFAULT 5, upr_email_refresh_rate int(5) DEFAULT 5, upr_email_signature longtext, upr_auto_append_email_sig tinyint(1) DEFAULT 0, upr_auto_append_note_sig tinyint(1) DEFAULT 0, upr_auto_close_popup_window tinyint(1) DEFAULT 0, PRIMARY KEY(upr_usr_id))
CREATE TABLE {{%custom_field_option}} ( cfo_id int(10) unsigned NOT NULL auto_increment, cfo_fld_id int(10) unsigned NOT NULL default 0, cfo_value varchar(128) NOT NULL default '', PRIMARY KEY (cfo_id), KEY icf_fld_id (cfo_fld_id))
CREATE TABLE {{%issue_access_log}} ( alg_id int unsigned NOT NULL auto_increment, alg_iss_id int unsigned NOT NULL, alg_usr_id int unsigned NOT NULL, alg_failed tinyint(1) NOT NULL DEFAULT 0, alg_item_id int unsigned NULL, alg_created datetime NOT NULL, alg_ip_address VARCHAR(15) NOT NULL, alg_item varchar(10) NULL, alg_url varchar(255) NULL, PRIMARY KEY (alg_id), KEY(alg_iss_id))
CREATE TABLE {{%project_custom_field}} ( pcf_id int(10) unsigned NOT NULL auto_increment, pcf_prj_id int(10) unsigned NOT NULL default 0, pcf_fld_id int(10) unsigned NOT NULL default 0, PRIMARY KEY (pcf_id), KEY pcf_prj_id (pcf_prj_id), KEY pcf_fld_id (pcf_fld_id))
CREATE TABLE {{%project_group}} ( pgr_prj_id int(11) unsigned not null, pgr_grp_id int(11) unsigned not null, PRIMARY KEY (pgr_prj_id, pgr_grp_id))
CREATE TABLE {{%email_response}} ( ere_id int(10) unsigned NOT NULL auto_increment, ere_title varchar(64) NOT NULL, ere_response_body text NOT NULL, PRIMARY KEY (ere_id), UNIQUE KEY ere_title (ere_title))
CREATE TABLE {{%project_severity}} ( sev_id smallint(3) unsigned NOT NULL auto_increment, sev_prj_id int(11) unsigned NOT NULL, sev_title varchar(64) NOT NULL default '', sev_description varchar(255) NULL, sev_rank TINYINT(1) NOT NULL, PRIMARY KEY (sev_id), UNIQUE KEY sev_title (sev_title, sev_prj_id))
CREATE TABLE {{%issue_checkin}} ( isc_id int(10) unsigned NOT NULL auto_increment, isc_iss_id int(10) unsigned NOT NULL default 0, isc_module varchar(255) NOT NULL default '', isc_filename varchar(255) NOT NULL default '', isc_old_version varchar(32) NOT NULL default '', isc_new_version varchar(32) NOT NULL default '', isc_created_date datetime NOT NULL default '0000-00-00 00:00:00', isc_username varchar(32) NOT NULL default '', isc_commit_msg text, PRIMARY KEY (isc_id), KEY isc_iss_id (isc_iss_id))
CREATE TABLE {{%project_phone_category}} ( phc_id int(11) unsigned NOT NULL auto_increment, phc_prj_id int(11) unsigned NOT NULL default 0, phc_title varchar(64) NOT NULL default '', PRIMARY KEY (phc_id), UNIQUE KEY uniq_category (phc_prj_id,phc_title), KEY phc_prj_id (phc_prj_id))
CREATE TABLE {{%project}} ( prj_id int(11) unsigned NOT NULL auto_increment, prj_created_date datetime NOT NULL default '0000-00-00 00:00:00', prj_title varchar(64) NOT NULL default '', prj_status set('active','archived') NOT NULL default 'active', prj_lead_usr_id int(11) unsigned NOT NULL default 0, prj_initial_sta_id int(10) unsigned NOT NULL default 0, prj_remote_invocation varchar(8) NOT NULL default 'disabled', prj_anonymous_post varchar(8) NOT NULL default 'disabled', prj_anonymous_post_options text, prj_outgoing_sender_name varchar(255) NOT NULL, prj_outgoing_sender_email varchar(255) NOT NULL, prj_mail_aliases varchar(255) NULL, prj_customer_backend varchar(64) NULL, prj_workflow_backend varchar(64) NULL, prj_segregate_reporter tinyint(1) DEFAULT 0, PRIMARY KEY (prj_id), UNIQUE KEY prj_title (prj_title), KEY prj_lead_usr_id (prj_lead_usr_id))
CREATE TABLE {{%issue_partner}} ( ipa_iss_id int(11) unsigned NOT NULL, ipa_par_code varchar(30) NOT NULL, ipa_created_date DATETIME NOT NULL, PRIMARY KEY(ipa_iss_id, ipa_par_code))
select max(ttc_id) from {{%time_tracking_category}} into @max_ttc_id;
CREATE TABLE {{%reminder_action}} ( rma_id INT(11) UNSIGNED NOT NULL AUTO_INCREMENT, rma_rem_id INT(11) UNSIGNED NOT NULL, rma_rmt_id TINYINT(3) UNSIGNED NOT NULL, rma_created_date DATETIME NOT NULL, rma_last_updated_date DATETIME NULL, rma_title VARCHAR(64) NOT NULL, rma_rank TINYINT(2) UNSIGNED NOT NULL, rma_alert_irc TINYINT(1) unsigned NOT NULL DEFAULT 0, rma_alert_group_leader TINYINT(1) unsigned NOT NULL DEFAULT 0, rma_boilerplate varchar(255) DEFAULT NULL, PRIMARY KEY(rma_id))
CREATE TABLE {{%irc_notice}} ( ino_id INT(11) UNSIGNED NOT NULL AUTO_INCREMENT, ino_prj_id int(11) NOT NULL, ino_iss_id INT(11) UNSIGNED NOT NULL, ino_created_date DATETIME NOT NULL, ino_message VARCHAR(255) NOT NULL, ino_status VARCHAR(8) NOT NULL DEFAULT 'pending', PRIMARY KEY(ino_id))
CREATE TABLE {{%project_status}} ( prs_id int(10) unsigned NOT NULL auto_increment, prs_prj_id int(10) unsigned NOT NULL, prs_sta_id int(10) unsigned NOT NULL, PRIMARY KEY (prs_id), KEY prs_prj_id (prs_prj_id, prs_sta_id))
