CREATE TABLE `user` ( `id` int(11) unsigned NOT NULL AUTO_INCREMENT, `country_id` int(11) unsigned NOT NULL, `type` enum('admin','author') NOT NULL, `name` varchar(20) NOT NULL, PRIMARY KEY (`id`), KEY `country_id` (`country_id`), CONSTRAINT `user_ibfk_1` FOREIGN KEY (`country_id`) REFERENCES `country` (`id`))
CREATE TABLE `comment` ( `id` int(11) NOT NULL AUTO_INCREMENT, `article_id` int(11) unsigned NOT NULL, `user_id` int(11) unsigned NOT NULL, `content` varchar(100) NOT NULL, PRIMARY KEY (`id`), KEY `article_id` (`article_id`), KEY `user_id` (`user_id`), CONSTRAINT `comment_ibfk_1` FOREIGN KEY (`article_id`) REFERENCES `article` (`id`), CONSTRAINT `comment_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`))
CREATE TABLE `article` ( `id` int(11) unsigned NOT NULL AUTO_INCREMENT, `user_id` int(11) unsigned NOT NULL DEFAULT 0, `published_at` datetime NOT NULL DEFAULT 0, `title` varchar(100) NOT NULL DEFAULT '', `content` text NOT NULL DEFAULT '', PRIMARY KEY (`id`), KEY `user_id` (`user_id`), CONSTRAINT `article_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `user` (`id`))
CREATE TABLE `country` ( `id` int(11) unsigned NOT NULL AUTO_INCREMENT, `name` varchar(20) NOT NULL, PRIMARY KEY (`id`))
