CREATE TABLE $wsluserscontacts (			id int(11) NOT NULL AUTO_INCREMENT,			user_id int(11) NOT NULL,			provider varchar(50) NOT NULL,			identifier varchar(255) NOT NULL,			full_name varchar(150) NOT NULL,			email varchar(255) NOT NULL,			profile_url varchar(255) NOT NULL,			photo_url varchar(255) NOT NULL,			UNIQUE KEY id (id),			KEY user_id (user_id)		)
