CREATE TABLE `posts_tags` (\r\n `id` int(11) NOT NULL AUTO_INCREMENT,\r\n `tag_id` int(11) NOT NULL,\r\n `post_id` int(11) NOT NULL,\r\n PRIMARY KEY (`id`),\r\n KEY `tag_id` (`tag_id`),\r\n KEY `post_id` (`post_id`),\r\n CONSTRAINT `posts_tags_ibfk_1` FOREIGN KEY (`tag_id`) REFERENCES `tags` (`id`),\r\n CONSTRAINT `posts_tags_ibfk_2` FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`)\r\n)
CREATE TABLE `settings` ( `id` int(11) NOT NULL AUTO_INCREMENT, `key` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `value` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL, PRIMARY KEY (`id`), UNIQUE KEY `UNIQ_E545A0C54E645A7E` (`key`))
CREATE TABLE `posts_tags` ( `tag_id` int(11) NOT NULL, `post_id` int(11) NOT NULL, PRIMARY KEY (`post_id`,`tag_id`), KEY `IDX_D5ECAD9F4B89032C` (`post_id`), KEY `IDX_D5ECAD9FBAD26311` (`tag_id`), CONSTRAINT `FK_D5ECAD9F4B89032C` FOREIGN KEY (`post_id`) REFERENCES `posts` (`id`) ON DELETE CASCADE, CONSTRAINT `FK_D5ECAD9FBAD26311` FOREIGN KEY (`tag_id`) REFERENCES `tags` (`id`) ON DELETE CASCADE)
