CREATE TABLE `categories` ( `id` tinyint(3) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Category id', `title` varchar(255) DEFAULT NULL COMMENT 'Category title', `description` varchar(512) NOT NULL DEFAULT '' COMMENT 'Category description', `alias` varchar(64) NOT NULL DEFAULT '' COMMENT 'Category alias', `parent_id` tinyint(3) unsigned DEFAULT NULL COMMENT 'Parent category', `lft` smallint(5) unsigned DEFAULT NULL COMMENT 'Left padding', `rgt` smallint(5) unsigned DEFAULT NULL COMMENT 'Right padding', `sort` tinyint(2) unsigned NOT NULL DEFAULT '0' COMMENT 'Sort index', `date_create` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT 'Create date', `date_update` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Update date', PRIMARY KEY (`id`), UNIQUE KEY `uni_alias` (`alias`), KEY `idx_rgt` (`rgt`), KEY `idx_lft` (`lft`))
