CREATE TABLE IF NOT EXISTS `wechat_public_accounts` (`id` int(10) unsigned NOT NULL COMMENT 'id', `wid` varchar(50) NOT NULL COMMENT '', `name` char(50) NOT NULL COMMENT '', `open_id` varchar(30) NOT NULL COMMENT 'openid', `head_url` varchar(500) NOT NULL COMMENT '', `description` varchar(500) NOT NULL COMMENT '', `verify_info` varchar(500) NOT NULL COMMENT '', `last_article_url` varchar(500) NOT NULL COMMENT '', `lasttime` datetime NOT NULL COMMENT '')
CREATE TABLE IF NOT EXISTS `wechat_keywords` (`id` int(11) NOT NULL COMMENT 'ID', `kid` bigint(20) unsigned NOT NULL COMMENT 'CRC32', `keyword` char(50) NOT NULL COMMENT '', `lasttime` datetime NOT NULL COMMENT '')
