CREATE TABLE `sc_intrusion` ( `id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(128) COLLATE utf8_unicode_ci NOT NULL, `tag` varchar(40) COLLATE utf8_unicode_ci DEFAULT NULL, `value` longtext COLLATE utf8_unicode_ci NOT NULL, `page` longtext COLLATE utf8_unicode_ci NOT NULL, `ip` varchar(40) COLLATE utf8_unicode_ci NOT NULL, `impact` int(11) NOT NULL, `filters` longtext COLLATE utf8_unicode_ci NOT NULL, `date` datetime NOT NULL, `uid` int(11) DEFAULT NULL, PRIMARY KEY (`id`), KEY `IDX_8595CE46539B0606` (`uid`))
