CREATE TABLE `themes` ( `id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(64) COLLATE utf8_unicode_ci NOT NULL, `type` smallint(6) NOT NULL, `displayname` varchar(64) COLLATE utf8_unicode_ci NOT NULL, `description` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `directory` varchar(64) COLLATE utf8_unicode_ci NOT NULL, `version` varchar(10) COLLATE utf8_unicode_ci NOT NULL, `contact` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `admin` smallint(6) NOT NULL, `user` smallint(6) NOT NULL, `system` smallint(6) NOT NULL, `state` smallint(6) NOT NULL, `xhtml` smallint(6) NOT NULL, PRIMARY KEY (`id`))
