CREATE TABLE faves ( id integer NOT NULL, user_id integer, zine_id integer, created_at timestamp without time zone, updated_at timestamp without time zone)
CREATE TABLE authorships ( author_id integer NOT NULL, zine_id integer NOT NULL)
CREATE TABLE comments ( id integer NOT NULL, user_id integer, zine_id integer, content text, created_at timestamp without time zone, updated_at timestamp without time zone)
CREATE TABLE schema_migrations ( version character varying(255) NOT NULL)
CREATE TABLE users ( id integer NOT NULL, email character varying(255) DEFAULT ''::character varying NOT NULL, encrypted_password character varying(255) DEFAULT ''::character varying NOT NULL, reset_password_token character varying(255), reset_password_sent_at timestamp without time zone, remember_created_at timestamp without time zone, sign_in_count integer DEFAULT 0, current_sign_in_at timestamp without time zone, last_sign_in_at timestamp without time zone, current_sign_in_ip character varying(255), last_sign_in_ip character varying(255), created_at timestamp without time zone, updated_at timestamp without time zone)
CREATE TABLE zines ( id integer NOT NULL, title character varying(255) NOT NULL, subtitle character varying(255), created_at timestamp without time zone, updated_at timestamp without time zone, published boolean DEFAULT true, full_text text, excerpt text, cover_image character varying(255), download_url character varying(255), added_at timestamp without time zone, author_count integer DEFAULT 0 NOT NULL, pdf character varying(255), legacy_id integer)
CREATE TABLE tags ( id integer NOT NULL, name character varying(255), user_id integer, zine_id integer, created_at timestamp without time zone, updated_at timestamp without time zone)
CREATE TABLE authors ( id integer NOT NULL, name character varying(255), created_at timestamp without time zone, updated_at timestamp without time zone)
